// Simple ZK proof for company email verification
// This demonstrates the concept of proving company email ownership
// without revealing the actual email address

fn main(
    domain_hash: pub Field,        // Hash of the company domain
    user_secret: Field,            // User's secret (known only to user)
    timestamp: pub u32,            // Timestamp when proof was created
    domain_type: pub u8,           // 0 = company domain, 1 = personal domain (should be 0)
) {
    // Verify domain is not a personal email provider
    assert(domain_type == 0, "personal email domains not allowed");

    // Verify timestamp is reasonable (not in the future, not too old)
    // For this demo, we just check it's not zero and positive
    assert(timestamp > 0, "invalid timestamp");

    // Create a hash that binds the user to the domain
    // This proves the user has knowledge of both the domain and their secret
    let proof_hash = domain_hash + user_secret + (timestamp as Field);

    // The proof is valid if all constraints pass
    assert(proof_hash != 0, "invalid proof");
}
